document.addEventListener("DOMContentLoaded",(function(){const heroImage=document.querySelector(".inicio-image img");if(heroImage){heroImage.onload=function(){document.body.classList.add("hero-loaded")}}window.addEventListener("load",(function(){if(document.querySelector(".back-to-top")){const backToTopBtn=document.querySelector(".back-to-top");if(!backToTopBtn.hasAttribute("data-configured")){backToTopBtn.setAttribute("data-configured","true");const toggleBackToTopButton=()=>{if(window.scrollY>300){backToTopBtn.classList.add("show")}else{backToTopBtn.classList.remove("show")}};window.addEventListener("scroll",toggleBackToTopButton);toggleBackToTopButton();backToTopBtn.addEventListener("click",(function(e){e.preventDefault();window.scrollTo({top:0,behavior:"smooth"})}))}}}))}));