(function(){"use strict";const BREAKPOINTS={mobile:768};function setupErrorHandling(){const originalConsoleError=console.error;console.error=function(...args){if(args[0]&&typeof args[0]==="object"&&args[0].message&&args[0].message.includes("Access to storage is not allowed")){return}originalConsoleError.apply(console,args)}}const animationManager={init(){if(typeof AOS==="undefined"){console.warn("AOS library not loaded");return}AOS.init({duration:800,easing:"ease-in-out",once:true,mirror:false,disable:window.innerWidth<BREAKPOINTS.mobile,startEvent:"DOMContentLoaded",offset:120});window.addEventListener("resize",this.handleResize)},handleResize(){if(!AOS)return;if(window.innerWidth<BREAKPOINTS.mobile){AOS.refreshHard()}else{AOS.refresh()}}};const navManager={init(){this.cacheElements();this.bindEvents();this.setupScrollMonitor()},cacheElements(){this.elements={header:document.getElementById("header"),mobileMenuBtn:document.querySelector(".mobile-menu-btn"),mobileMenu:document.querySelector(".main-menu"),mobileMenuOverlay:document.querySelector(".mobile-menu-overlay"),backToTopBtn:document.getElementById("back-to-top"),menuLinks:document.querySelectorAll('a[href^="#"]')}},bindEvents(){if(this.elements.mobileMenuBtn){this.elements.mobileMenuBtn.addEventListener("click",this.toggleMobileMenu.bind(this))}if(this.elements.mobileMenuOverlay){this.elements.mobileMenuOverlay.addEventListener("click",this.closeMobileMenu.bind(this))}if(this.elements.backToTopBtn){this.elements.backToTopBtn.addEventListener("click",this.scrollToTop.bind(this))}this.elements.menuLinks.forEach((link=>{link.addEventListener("click",this.handleSmoothScroll.bind(this))}))},setupScrollMonitor(){window.addEventListener("scroll",(()=>{this.updateHeaderOnScroll();this.updateBackToTopButton()}))},updateHeaderOnScroll(){if(!this.elements.header)return;if(window.scrollY>100){this.elements.header.classList.add("scrolled")}else{this.elements.header.classList.remove("scrolled")}},updateBackToTopButton(){if(!this.elements.backToTopBtn)return;if(window.scrollY>300){this.elements.backToTopBtn.classList.add("show")}else{this.elements.backToTopBtn.classList.remove("show")}},toggleMobileMenu(){if(!this.elements.mobileMenu)return;this.elements.mobileMenu.classList.toggle("active");document.body.classList.toggle("menu-open")},closeMobileMenu(){if(!this.elements.mobileMenu)return;this.elements.mobileMenu.classList.remove("active");document.body.classList.remove("menu-open")},scrollToTop(){window.scrollTo({top:0,behavior:"smooth"})},handleSmoothScroll(e){const href=e.currentTarget.getAttribute("href");if(!href.startsWith("#"))return;e.preventDefault();const target=document.querySelector(href);if(!target)return;this.closeMobileMenu();const headerHeight=this.elements.header?.offsetHeight||0;const targetPosition=target.offsetTop-headerHeight-20;window.scrollTo({top:targetPosition,behavior:"smooth"})}};const faqManager={init(){this.setupAccordion()},setupAccordion(){const faqItems=document.querySelectorAll(".faq-item");if(faqItems.length===0)return;faqItems.forEach((item=>{const question=item.querySelector(".faq-question");if(!question)return;question.addEventListener("click",(()=>{this.toggleFaqItem(item)}))}))},toggleFaqItem(selectedItem){const isActive=selectedItem.classList.contains("active");document.querySelectorAll(".faq-item").forEach((item=>{item.classList.remove("active")}));if(!isActive){selectedItem.classList.add("active")}}};const externalLinkManager={init(){this.setupExternalLinks()},setupExternalLinks(){const externalLinks=document.querySelectorAll('a[href^="http"], a[href^="https"]');externalLinks.forEach((link=>{if(!link.hostname.includes(window.location.hostname)){link.setAttribute("rel","noopener");link.setAttribute("target","_blank")}}))}};document.addEventListener("DOMContentLoaded",(function(){setupErrorHandling();animationManager.init();navManager.init();faqManager.init();externalLinkManager.init()}))})();